// VFR_HUD provides flight data in a compact format
const vfr = msg.payload;

if (!vfr) return null;

// Extract flight data
const airspeed = vfr.airspeed || 0;           // m/s
const groundspeed = vfr.groundspeed || 0;     // m/s
const altitude = vfr.alt || 0;                // meters MSL
const climb = vfr.climb || 0;                 // m/s vertical speed
const heading = vfr.heading || 0;             // degrees
const throttle = vfr.throttle || 0;           // 0-100%

// Convert speeds to km/h for display
const airspeedKmh = (airspeed * 3.6).toFixed(1);
const groundspeedKmh = (groundspeed * 3.6).toFixed(1);

// Climb rate with direction indicator
const climbRate = climb.toFixed(1);
const climbIndicator = climb > 0.5 ? 'â†‘' : (climb < -0.5 ? 'â†“' : 'â†’');
const climbColor = climb > 0.5 ? 'green' : (climb < -0.5 ? 'orange' : 'gray');

return [
    { payload: altitude.toFixed(1), topic: 'flight/altitude' },
    { payload: airspeedKmh, topic: 'flight/airspeed' },
    { payload: groundspeedKmh, topic: 'flight/groundspeed' },
    { payload: climbRate, topic: 'flight/climb', indicator: climbIndicator, color: climbColor },
    { payload: heading, topic: 'flight/heading' },
    { payload: throttle, topic: 'flight/throttle' }
];

// GLOBAL_POSITION_INT provides relative altitude (from home)
const pos = msg.payload;

if (!pos) return null;

// Relative altitude (mm to m)
const relativeAlt = pos.relative_alt ? pos.relative_alt / 1000 : 0;

return { 
    payload: relativeAlt.toFixed(1), 
    topic: 'flight/relative_altitude' 
};

// SYS_STATUS provides battery information
const sys = msg.payload;

if (!sys) return null;

// Extract battery data
const batteryPercent = sys.battery_remaining || 0;  // 0-100%
const voltage = sys.voltage_battery ? sys.voltage_battery / 1000 : 0;  // mV to V
const current = sys.current_battery ? sys.current_battery / 100 : 0;   // cA to A

// Calculate power consumption (W)
const power = (voltage * current).toFixed(1);

// Battery health indicator
let healthIcon = 'ðŸŸ¢';
let healthText = 'Good';
if (batteryPercent < 20) {
    healthIcon = 'ðŸ”´';
    healthText = 'CRITICAL';
} else if (batteryPercent < 30) {
    healthIcon = 'ðŸŸ ';
    healthText = 'Low';
} else if (batteryPercent < 50) {
    healthIcon = 'ðŸŸ¡';
    healthText = 'Fair';
}

return [
    { payload: batteryPercent, topic: 'battery/percent' },
    { payload: voltage.toFixed(2), topic: 'battery/voltage' },
    { payload: current.toFixed(2), topic: 'battery/current' },
    { payload: power, topic: 'battery/power' }
];
